"use strict";(self.webpackChunk=self.webpackChunk||[]).push([["1240"],{5267:function(n,t,s){s.r(t),s.d(t,{default:()=>e});var d=s(5598);let a=["innerHTML"],e={setup:()=>({html:""}),render:()=>((0,d.wg)(),(0,d.iD)("div",{class:"van-doc-markdown-body",innerHTML:'<h1>Coupon 优惠券选择器</h1>\n<div class="van-doc-card"><h3 id="jie-shao" tabindex="-1">介绍</h3>\n<p>用于优惠券的兑换和选择。</p>\n</div><div class="van-doc-card"><h3 id="yin-ru" tabindex="-1">引入</h3>\n<p>通过以下方式来全局注册组件，更多注册方式请参考<a href="#/zh-CN/advanced-usage#zu-jian-zhu-ce" target="_blank">组件注册</a>。</p>\n<pre><code class="language-js"><span class="hljs-keyword">import</span> { createApp } <span class="hljs-keyword">from</span> <span class="hljs-string">&#x27;vue&#x27;</span>;\n<span class="hljs-keyword">import</span> { <span class="hljs-title class_">CouponCell</span>, <span class="hljs-title class_">CouponList</span> } <span class="hljs-keyword">from</span> <span class="hljs-string">&#x27;vant&#x27;</span>;\n\n<span class="hljs-keyword">const</span> app = <span class="hljs-title function_">createApp</span>();\napp.<span class="hljs-title function_">use</span>(<span class="hljs-title class_">CouponCell</span>);\napp.<span class="hljs-title function_">use</span>(<span class="hljs-title class_">CouponList</span>);\n</code></pre>\n</div><h2 id="dai-ma-yan-shi" tabindex="-1">代码演示</h2>\n<div class="van-doc-card"><h3 id="ji-chu-yong-fa" tabindex="-1">基础用法</h3>\n<pre><code class="language-html"><span class="hljs-comment">&lt;!-- 优惠券单元格 --&gt;</span>\n<span class="hljs-tag">&lt;<span class="hljs-name">van-coupon-cell</span>\n  <span class="hljs-attr">:coupons</span>=<span class="hljs-string">&quot;coupons&quot;</span>\n  <span class="hljs-attr">:chosen-coupon</span>=<span class="hljs-string">&quot;chosenCoupon&quot;</span>\n  @<span class="hljs-attr">click</span>=<span class="hljs-string">&quot;showList = true&quot;</span>\n/&gt;</span>\n<span class="hljs-comment">&lt;!-- 优惠券列表 --&gt;</span>\n<span class="hljs-tag">&lt;<span class="hljs-name">van-popup</span>\n  <span class="hljs-attr">v-model:show</span>=<span class="hljs-string">&quot;showList&quot;</span>\n  <span class="hljs-attr">round</span>\n  <span class="hljs-attr">position</span>=<span class="hljs-string">&quot;bottom&quot;</span>\n  <span class="hljs-attr">style</span>=<span class="hljs-string">&quot;height: 90%; padding-top: 4px;&quot;</span>\n&gt;</span>\n  <span class="hljs-tag">&lt;<span class="hljs-name">van-coupon-list</span>\n    <span class="hljs-attr">:coupons</span>=<span class="hljs-string">&quot;coupons&quot;</span>\n    <span class="hljs-attr">:chosen-coupon</span>=<span class="hljs-string">&quot;chosenCoupon&quot;</span>\n    <span class="hljs-attr">:disabled-coupons</span>=<span class="hljs-string">&quot;disabledCoupons&quot;</span>\n    @<span class="hljs-attr">change</span>=<span class="hljs-string">&quot;onChange&quot;</span>\n    @<span class="hljs-attr">exchange</span>=<span class="hljs-string">&quot;onExchange&quot;</span>\n  /&gt;</span>\n<span class="hljs-tag">&lt;/<span class="hljs-name">van-popup</span>&gt;</span>\n</code></pre>\n<pre><code class="language-js"><span class="hljs-keyword">import</span> { ref } <span class="hljs-keyword">from</span> <span class="hljs-string">&#x27;vue&#x27;</span>;\n\n<span class="hljs-keyword">export</span> <span class="hljs-keyword">default</span> {\n  <span class="hljs-title function_">setup</span>(<span class="hljs-params"></span>) {\n    <span class="hljs-keyword">const</span> coupon = {\n      <span class="hljs-attr">available</span>: <span class="hljs-number">1</span>,\n      <span class="hljs-attr">condition</span>: <span class="hljs-string">&#x27;无门槛\\n最多优惠12元&#x27;</span>,\n      <span class="hljs-attr">reason</span>: <span class="hljs-string">&#x27;&#x27;</span>,\n      <span class="hljs-attr">value</span>: <span class="hljs-number">150</span>,\n      <span class="hljs-attr">name</span>: <span class="hljs-string">&#x27;优惠券名称&#x27;</span>,\n      <span class="hljs-attr">startAt</span>: <span class="hljs-number">1489104000</span>,\n      <span class="hljs-attr">endAt</span>: <span class="hljs-number">1514592000</span>,\n      <span class="hljs-attr">valueDesc</span>: <span class="hljs-string">&#x27;1.5&#x27;</span>,\n      <span class="hljs-attr">unitDesc</span>: <span class="hljs-string">&#x27;元&#x27;</span>,\n    };\n\n    <span class="hljs-keyword">const</span> coupons = <span class="hljs-title function_">ref</span>([coupon]);\n    <span class="hljs-keyword">const</span> showList = <span class="hljs-title function_">ref</span>(<span class="hljs-literal">false</span>);\n    <span class="hljs-keyword">const</span> chosenCoupon = <span class="hljs-title function_">ref</span>(-<span class="hljs-number">1</span>);\n\n    <span class="hljs-keyword">const</span> <span class="hljs-title function_">onChange</span> = (<span class="hljs-params">index</span>) =&gt; {\n      showList.<span class="hljs-property">value</span> = <span class="hljs-literal">false</span>;\n      chosenCoupon.<span class="hljs-property">value</span> = index;\n    };\n    <span class="hljs-keyword">const</span> <span class="hljs-title function_">onExchange</span> = (<span class="hljs-params">code</span>) =&gt; {\n      coupons.<span class="hljs-property">value</span>.<span class="hljs-title function_">push</span>(coupon);\n    };\n\n    <span class="hljs-keyword">return</span> {\n      coupons,\n      showList,\n      onChange,\n      onExchange,\n      chosenCoupon,\n      <span class="hljs-attr">disabledCoupons</span>: [coupon],\n    };\n  },\n};\n</code></pre>\n</div><h2 id="api" tabindex="-1">API</h2>\n<div class="van-doc-card"><h3 id="couponcell-props" tabindex="-1">CouponCell Props</h3>\n<table>\n<thead>\n<tr>\n<th>参数</th>\n<th>说明</th>\n<th>类型</th>\n<th>默认值</th>\n</tr>\n</thead>\n<tbody>\n<tr>\n<td>title</td>\n<td>单元格标题</td>\n<td><em>string</em></td>\n<td><code>优惠券</code></td>\n</tr>\n<tr>\n<td>chosen-coupon</td>\n<td>当前选中优惠券的索引</td>\n<td><em>number | number[]</em></td>\n<td><code>-1</code></td>\n</tr>\n<tr>\n<td>coupons</td>\n<td>可用优惠券列表</td>\n<td><em>Coupon[]</em></td>\n<td><code>[]</code></td>\n</tr>\n<tr>\n<td>editable</td>\n<td>能否切换优惠券</td>\n<td><em>boolean</em></td>\n<td><code>true</code></td>\n</tr>\n<tr>\n<td>border</td>\n<td>是否显示内边框</td>\n<td><em>boolean</em></td>\n<td><code>true</code></td>\n</tr>\n<tr>\n<td>currency</td>\n<td>货币符号</td>\n<td><em>string</em></td>\n<td><code>\xa5</code></td>\n</tr>\n</tbody>\n</table>\n</div><div class="van-doc-card"><h3 id="couponlist-props" tabindex="-1">CouponList Props</h3>\n<table>\n<thead>\n<tr>\n<th>参数</th>\n<th>说明</th>\n<th>类型</th>\n<th>默认值</th>\n</tr>\n</thead>\n<tbody>\n<tr>\n<td>v-model:code</td>\n<td>当前输入的兑换码</td>\n<td><em>string</em></td>\n<td>-</td>\n</tr>\n<tr>\n<td>chosen-coupon</td>\n<td>当前选中优惠券的索引,支持多选（类型为 <code>[]</code>）</td>\n<td><em>number | number[]</em></td>\n<td><code>-1</code></td>\n</tr>\n<tr>\n<td>coupons</td>\n<td>可用优惠券列表</td>\n<td><em>CouponInfo[]</em></td>\n<td><code>[]</code></td>\n</tr>\n<tr>\n<td>disabled-coupons</td>\n<td>不可用优惠券列表</td>\n<td><em>CouponInfo[]</em></td>\n<td><code>[]</code></td>\n</tr>\n<tr>\n<td>enabled-title</td>\n<td>可用优惠券列表标题</td>\n<td><em>string</em></td>\n<td><code>可使用优惠券</code></td>\n</tr>\n<tr>\n<td>disabled-title</td>\n<td>不可用优惠券列表标题</td>\n<td><em>string</em></td>\n<td><code>不可使用优惠券</code></td>\n</tr>\n<tr>\n<td>exchange-button-text</td>\n<td>兑换按钮文字</td>\n<td><em>string</em></td>\n<td><code>兑换</code></td>\n</tr>\n<tr>\n<td>exchange-button-loading</td>\n<td>是否显示兑换按钮加载动画</td>\n<td><em>boolean</em></td>\n<td><code>false</code></td>\n</tr>\n<tr>\n<td>exchange-button-disabled</td>\n<td>是否禁用兑换按钮</td>\n<td><em>boolean</em></td>\n<td><code>false</code></td>\n</tr>\n<tr>\n<td>exchange-min-length</td>\n<td>兑换码最小长度</td>\n<td><em>number</em></td>\n<td><code>1</code></td>\n</tr>\n<tr>\n<td>displayed-coupon-index</td>\n<td>滚动至特定优惠券位置</td>\n<td><em>number</em></td>\n<td>-</td>\n</tr>\n<tr>\n<td>show-close-button</td>\n<td>是否显示列表底部按钮</td>\n<td><em>boolean</em></td>\n<td><code>true</code></td>\n</tr>\n<tr>\n<td>close-button-text</td>\n<td>列表底部按钮文字</td>\n<td><em>string</em></td>\n<td><code>不使用优惠</code></td>\n</tr>\n<tr>\n<td>input-placeholder</td>\n<td>输入框文字提示</td>\n<td><em>string</em></td>\n<td><code>请输入优惠码</code></td>\n</tr>\n<tr>\n<td>show-exchange-bar</td>\n<td>是否展示兑换栏</td>\n<td><em>boolean</em></td>\n<td><code>true</code></td>\n</tr>\n<tr>\n<td>currency</td>\n<td>货币符号</td>\n<td><em>string</em></td>\n<td><code>\xa5</code></td>\n</tr>\n<tr>\n<td>empty-image</td>\n<td>列表为空时的占位图</td>\n<td><em>string</em></td>\n<td>-</td>\n</tr>\n<tr>\n<td>show-count</td>\n<td>是否展示可用 / 不可用数量</td>\n<td><em>boolean</em></td>\n<td><code>true</code></td>\n</tr>\n</tbody>\n</table>\n</div><div class="van-doc-card"><h3 id="couponlist-events" tabindex="-1">CouponList Events</h3>\n<table>\n<thead>\n<tr>\n<th>事件名</th>\n<th>说明</th>\n<th>回调参数</th>\n</tr>\n</thead>\n<tbody>\n<tr>\n<td>change</td>\n<td>优惠券切换回调</td>\n<td>index, 选中优惠券的索引</td>\n</tr>\n<tr>\n<td>exchange</td>\n<td>兑换优惠券回调</td>\n<td>code, 兑换码</td>\n</tr>\n</tbody>\n</table>\n</div><div class="van-doc-card"><h3 id="couponlist-slots" tabindex="-1">CouponList Slots</h3>\n<table>\n<thead>\n<tr>\n<th>名称</th>\n<th>说明</th>\n</tr>\n</thead>\n<tbody>\n<tr>\n<td>list-footer</td>\n<td>优惠券列表底部</td>\n</tr>\n<tr>\n<td>disabled-list-footer</td>\n<td>不可用优惠券列表底部</td>\n</tr>\n<tr>\n<td>list-button</td>\n<td>自定义底部按钮</td>\n</tr>\n</tbody>\n</table>\n</div><div class="van-doc-card"><h3 id="couponinfo-shu-ju-jie-gou" tabindex="-1">CouponInfo 数据结构</h3>\n<table>\n<thead>\n<tr>\n<th>键名</th>\n<th>说明</th>\n<th>类型</th>\n</tr>\n</thead>\n<tbody>\n<tr>\n<td>id</td>\n<td>优惠券 id</td>\n<td><em>string</em></td>\n</tr>\n<tr>\n<td>name</td>\n<td>优惠券名称</td>\n<td><em>string</em></td>\n</tr>\n<tr>\n<td>condition</td>\n<td>满减条件</td>\n<td><em>string</em></td>\n</tr>\n<tr>\n<td>startAt</td>\n<td>卡有效开始时间 (时间戳, 单位秒)</td>\n<td><em>number</em></td>\n</tr>\n<tr>\n<td>endAt</td>\n<td>卡失效日期 (时间戳, 单位秒)</td>\n<td><em>number</em></td>\n</tr>\n<tr>\n<td>description</td>\n<td>描述信息，优惠券可用时展示</td>\n<td><em>string</em></td>\n</tr>\n<tr>\n<td>reason</td>\n<td>不可用原因，优惠券不可用时展示</td>\n<td><em>string</em></td>\n</tr>\n<tr>\n<td>value</td>\n<td>折扣券优惠金额，单位分</td>\n<td><em>number</em></td>\n</tr>\n<tr>\n<td>valueDesc</td>\n<td>折扣券优惠金额文案</td>\n<td><em>string</em></td>\n</tr>\n<tr>\n<td>unitDesc</td>\n<td>单位文案</td>\n<td><em>string</em></td>\n</tr>\n</tbody>\n</table>\n</div><div class="van-doc-card"><h3 id="lei-xing-ding-yi" tabindex="-1">类型定义</h3>\n<p>组件导出以下类型定义：</p>\n<pre><code class="language-ts"><span class="hljs-keyword">import</span> <span class="hljs-keyword">type</span> { <span class="hljs-title class_">CouponCellProps</span>, <span class="hljs-title class_">CouponListProps</span>, <span class="hljs-title class_">CouponInfo</span> } <span class="hljs-keyword">from</span> <span class="hljs-string">&#x27;vant&#x27;</span>;\n</code></pre>\n</div><h2 id="zhu-ti-ding-zhi" tabindex="-1">主题定制</h2>\n<div class="van-doc-card"><h3 id="yang-shi-bian-liang" tabindex="-1">样式变量</h3>\n<p>组件提供了下列 CSS 变量，可用于自定义样式，使用方法请参考 <a href="#/zh-CN/config-provider" target="_blank">ConfigProvider 组件</a>。</p>\n<table>\n<thead>\n<tr>\n<th>名称</th>\n<th>默认值</th>\n<th>描述</th>\n</tr>\n</thead>\n<tbody>\n<tr>\n<td>--van-coupon-margin</td>\n<td><em>0 var(--van-padding-sm) var(--van-padding-sm)</em></td>\n<td>-</td>\n</tr>\n<tr>\n<td>--van-coupon-content-height</td>\n<td><em>84px</em></td>\n<td>-</td>\n</tr>\n<tr>\n<td>--van-coupon-content-padding</td>\n<td><em>14px 0</em></td>\n<td>-</td>\n</tr>\n<tr>\n<td>--van-coupon-content-text-color</td>\n<td><em>var(--van-text-color)</em></td>\n<td>-</td>\n</tr>\n<tr>\n<td>--van-coupon-background</td>\n<td><em>var(--van-background-2)</em></td>\n<td>-</td>\n</tr>\n<tr>\n<td>--van-coupon-active-background</td>\n<td><em>var(--van-active-color)</em></td>\n<td>-</td>\n</tr>\n<tr>\n<td>--van-coupon-radius</td>\n<td><em>var(--van-radius-lg)</em></td>\n<td>-</td>\n</tr>\n<tr>\n<td>--van-coupon-shadow</td>\n<td><em>0 0 4px rgba(0, 0, 0, 0.1)</em></td>\n<td>-</td>\n</tr>\n<tr>\n<td>--van-coupon-head-width</td>\n<td><em>96px</em></td>\n<td>-</td>\n</tr>\n<tr>\n<td>--van-coupon-amount-color</td>\n<td><em>var(--van-danger-color)</em></td>\n<td>-</td>\n</tr>\n<tr>\n<td>--van-coupon-amount-font-size</td>\n<td><em>30px</em></td>\n<td>-</td>\n</tr>\n<tr>\n<td>--van-coupon-currency-font-size</td>\n<td><em>40%</em></td>\n<td>-</td>\n</tr>\n<tr>\n<td>--van-coupon-name-font-size</td>\n<td><em>var(--van-font-size-md)</em></td>\n<td>-</td>\n</tr>\n<tr>\n<td>--van-coupon-disabled-text-color</td>\n<td><em>var(--van-text-color-2)</em></td>\n<td>-</td>\n</tr>\n<tr>\n<td>--van-coupon-description-padding</td>\n<td><em>var(--van-padding-xs) var(--van-padding-md)</em></td>\n<td>-</td>\n</tr>\n<tr>\n<td>--van-coupon-description-border-color</td>\n<td><em>var(--van-border-color)</em></td>\n<td>-</td>\n</tr>\n<tr>\n<td>--van-coupon-checkbox-color</td>\n<td><em>var(--van-danger-color)</em></td>\n<td>-</td>\n</tr>\n<tr>\n<td>--van-coupon-list-background</td>\n<td><em>var(--van-background)</em></td>\n<td>-</td>\n</tr>\n<tr>\n<td>--van-coupon-list-field-padding</td>\n<td><em>5px 0 5px var(--van-padding-md)</em></td>\n<td>-</td>\n</tr>\n<tr>\n<td>--van-coupon-list-exchange-button-height</td>\n<td><em>32px</em></td>\n<td>-</td>\n</tr>\n<tr>\n<td>--van-coupon-list-close-button-height</td>\n<td><em>40px</em></td>\n<td>-</td>\n</tr>\n<tr>\n<td>--van-coupon-list-empty-tip-color</td>\n<td><em>var(--van-text-color-2)</em></td>\n<td>-</td>\n</tr>\n<tr>\n<td>--van-coupon-list-empty-tip-font-size</td>\n<td><em>var(--van-font-size-md)</em></td>\n<td>-</td>\n</tr>\n<tr>\n<td>--van-coupon-list-empty-tip-line-height</td>\n<td><em>var(--van-line-height-md)</em></td>\n<td>-</td>\n</tr>\n<tr>\n<td>--van-coupon-cell-selected-text-color</td>\n<td><em>var(--van-text-color)</em></td>\n<td>-</td>\n</tr>\n</tbody>\n</table>\n</div>'},null,8,a))}}}]);